function dodSetCookie(a,b,c){document.cookie=a+"="+b+((c==null)?"":";expires="+c.toUTCString())}function dodRemoveCookie(a){var b=new Date();b.setDate(b.getDate()-365);dodSetCookie(a,"null",b)}function dodGetCookie(a){if(document.cookie.length>0){var b=document.cookie.indexOf(a+"=");var c="";if(b!=-1){b=b+a.length+1;c=document.cookie.indexOf(";",b);if(c==-1){c=document.cookie.length}return unescape(document.cookie.substring(b,c))}return""}}