jQuery.noConflict();

  function createFlip(){
    jQuery(".morefeat div:not(.revert)").bind("click",function(){
          var $this = jQuery(this);
          $divcont = "#"+$this.attr("rel");
          jQuery($divcont).flip({
            content: $this.attr("rev"),
            speed: $speedFlip,
            onEnd: revert
          })
          return false;
    })
  }
  
  function revert(){
    jQuery(".revert").bind("click",function(){

      var $this = jQuery(this);
      $divcont = "#"+$this.attr("rel");
      jQuery($divcont).revertFlip();
      setTimeout('createFlip();', ($speedFlip*5)+150);
      return false;
    });
  }
(jQuery);